from decimal import *
getcontext().prec = 150
getcontext().rounding = ROUND_HALF_UP
decpi = Decimal("3.")
def decsin(x):
	x = x%(2*decpi)
	i, lasts, s, fact, num, sign = Decimal('1'), Decimal('0'), Decimal(str(x)), Decimal('1'), Decimal(str(x)), Decimal('1')
	while s != lasts:
		lasts = s
		i += 2
		fact *= i * (i-1)
		num *= x * x
		sign *= -1
		s += num / fact * sign
	return Decimal(str(s))

def getVal(x):
    return Decimal(str(a))*Decimal(str(x)) + Decimal(str(b))*decsin(x)
a, b, c = map(int, input().split())
lo = Decimal('0')
hi = Decimal('1000000')
for i in range(1000):
    mid = Decimal(str(lo + hi))/Decimal('2')
    if getVal(mid) > c:
        hi = mid
    else:
        lo = mid
